import Foundation

enum SubarraySumsDivisibleByK {
    
    class Solution {
        func subarraysDivByK(_ nums: [Int], _ k: Int) -> Int {
            var map: [Int: Int] = [:]
            map[0] = 1
            
            var sum: Int = 0
            nums.forEach {
                sum += $0
                let trunc = sum % k
                map[trunc < 0 ? trunc + k : trunc, default: 0] += 1
            }
            
            var count: Int = 0
            
            for (_, value) in map where value > 1 {
                if value == 2 {
                    count += 1
                } else {
                    count += ((value - 1) * (value)) / 2
                }
            }
            
            return count
        }
    }
}

